import { useState } from 'react';
import './App.css';
import AddTasks from './components/AddTasks';

import SAMPLE_TASKS from './data/sample-tasks.json'

function App() {
  const [tasks, setTasks] = useState(SAMPLE_TASKS)
  return (
    <div className="App">
      {
        tasks.map(task => {
          return <div>{task.task_name}</div>
        })
      }

      <AddTasks />
    </div>
  );
}

export default App;
